function toggleNav(){const e=document.querySelector("label.hamburger-btn"),t=document.querySelector("#nav-drawer");e.addEventListener("click",(function(){t.style.cssText+="transform: translateX(0)"}))}function closeNav(){const e=document.querySelector(".material-icons.close"),t=document.querySelector("#nav-drawer");e.addEventListener("click",(function(){t.style.cssText+="transform: translateX(100%)"}))}function accordion(){document.addEventListener("click",(function(e){if(e.target.classList.contains("accordion-toggle")){var t=document.querySelector(e.target.hash);if(t)if(e.preventDefault(),t.classList.contains("active"))t.classList.remove("active");else{for(var n=document.querySelectorAll(".accordion-content.active"),c=0;c<n.length;c++)n[c].classList.remove("active");t.classList.toggle("active")}}}))}function readMore(){var e=document.getElementById("dots"),t=document.getElementById("more"),n=document.getElementById("myBtn");"none"===e.style.display?(e.style.display="inline",n.innerHTML="Read more",t.style.display="none"):(e.style.display="none",n.innerHTML="Read less",t.style.display="inline")}toggleNav(),closeNav(),accordion();