function toggleNav(){const e=document.querySelector("label.hamburger-btn"),t=document.querySelector("#nav-drawer");e.addEventListener("click",(function(){t.style.cssText+="transform: translateX(0)"}))}function closeNav(){const e=document.querySelector(".material-icons.close"),t=document.querySelector("#nav-drawer");e.addEventListener("click",(function(){t.style.cssText+="transform: translateX(100%)"}))}function accordion(){document.addEventListener("click",(function(e){if(e.target.classList.contains("accordion-toggle")){var t=document.querySelector(e.target.hash);if(t)if(e.preventDefault(),t.classList.contains("active"))t.classList.remove("active");else{for(var c=document.querySelectorAll(".accordion-content.active"),n=0;n<c.length;n++)c[n].classList.remove("active");t.classList.toggle("active")}}}))}toggleNav(),closeNav(),accordion();